import { Component } from "@angular/core";

@Component({
  selector: "app-login",
  templateUrl: "./login.component.html",
  styleUrls: ["./login.component.scss"],
})
export class LoginComponent {
  username = "";
  password = "";
  errorMsg = "";

  login() {
    if (this.username.trim().length === 0) {
      this.errorMsg = "Username is required";
    } else if (this.password.trim().length === 0) {
      this.errorMsg = "Password is required";
    }
  }
}
<div class="login-container">
  <img class="film" src="/assets/images/film.png" alt="" />
  <div class="logo">Rotten Potatoes</div>
  <div class="desc">
    Rotten Potatoes is the world's most trustes recommandation resource. One of
    the reasons why Rotten Potatoes has gained such a strong reputation is
    because of its unique rating system. Rather than simply providing an average
    score based on user ratings.
  </div>

  <div class="login-form">
    <h2>Login Account</h2>
    <div class="form-group">
      <label for="username">Username</label>
      <input
        type="text"
        name="username"
        class="form-control"
        [(ngModel)]="username"
        placeholder="Enter Username"
      />
    </div>
    <div class="form-group">
      <label for="password">Password</label>
      <input
        type="password"
        name="password"
        class="form-control"
        [(ngModel)]="password"
        placeholder="Enter Password"
      />
    </div>
    <div class="btn-container">
      <div class="error">{{ errorMsg }}</div>
      <button class="btn btn-dark" (click)="login()">Submit</button>
    </div>
  </div>
</div>
