import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {

}
<div class="login-container">
  <img class="film" src="/assets/images/film.png" alt="" />
  <div class="logo">Rotten Potatoes</div>
  <div class="desc">
    Rotten Potatoes is the world's most trustes recommandation resource. One of
    the reasons why Rotten Potatoes has gained such a strong reputation is
    because of its unique rating system. Rather than simply providing an average
    score based on user ratings, Rotten Potatoes uses a complex algorithm that
    takes into account a variety of factors, such as the number of reviews, the
    quality of the reviews, and the credibility of the reviewers themselves.
  </div>
</div>
